/**
 * 设备与云端交互测试
 */
var deviceShadow = require("../src/index.js");

console.log("=================================");
console.log("        deviceShadow test       ");
console.log("=================================");

//设备三元组
var device = {
    productKey: 'xxxxxx',
    deviceName: "xxxxxx",
    deviceSecret: "xxxxxx"
};

// 初始化 device shadow
function initDeviceShadow() 
{
  deviceShadow.bindDevID(device);
  deviceShadow.start(deviceShadowInitCB);
}

// 设备初始化回调函数
function deviceShadowInitCB(err) {
  if (err) 
  {
    console.log('连接 LD 失败, err = ' + err);
    // 再次重连
    setTimeout(initDeviceShadow, 2000);
  } 
  else 
  {
    console.log(' 连接 LD 成功 ');  
    registAllCloudNotify();
  }
}

// 注册所有云端设置设备端属性的处理函数
function registAllCloudNotify() 
{
  client.addDevSetPropertyNotify('batteryAlarm', cloudSetProperty);
}

// 设备端本地处理函数，将会注册到云端调用
function cloudSetProperty(val) 
{
  console.log('云端设置batteryAlarm=' + val);
}

// 周期性调用
var t1 = setInterval(function () {
	
	//每秒检测一次网卡是否连上网络？
  var ip = WIFI.getip();
  if (ip != null) 
  {
    clearInterval(t1);

    console.log('开始链接 ' + 'LD Platform');
    
    process.nextTick(initDeviceShadow);
  }
}, 1000);

